import React, { useState } from "react";
import "./Weather.css";
import axios from "axios";
import { GEO_API_URL, geoApiOptions } from "../api.js";

import search_icon from "../Assets/search.png";
import clear_icon from "../Assets/clear.png";
import cloud_icon from "../Assets/cloud.png";
import drizzle_icon from "../Assets/drizzle.png";
import rain_icon from "../Assets/rain.png";
import snow_icon from "../Assets/snow.png";
import wind_icon from "../Assets/wind.png";
import humidity_icon from "../Assets/humidity.png";
import question_mark from "../Assets/question-mark.png";

const Weather = () => {
  let api_key = "e28b36a32d1041b6adbc18b037cc26e6";

  const [value, setValue] = useState("");

  const onChange = async (e) => {
    setValue(e.target.value);
    try {
      const response = await fetch(GEO_API_URL, geoApiOptions);
      const result = await response.json();
      console.log(result.city);
    } catch (error) {
      console.error(error);
    }
  };

  const [data, setData] = useState({
    img: question_mark,
    temp: "xx °C",
    location: "///////",
    humidity: "xx %",
    wind: "xx km/h",
  });

  const [err, setErr] = useState("");

  const search = () => {
    setErr("");
    // const inputValue = document.getElementsByClassName("cityInput");

    if (value === "") {
      return 0;
    }

    let url = `https://api.openweathermap.org/data/2.5/weather?q=${value}&units=Metric&appid=${api_key}`;

    axios
      .get(url)
      .then((response) => {
        let img_icon;

        if (response.data.weather[0].main === "Clear") {
          img_icon = clear_icon;
        } else if (response.data.weather[0].main === "Cloud") {
          img_icon = cloud_icon;
        } else if (response.data.weather[0].main === "Drizzle") {
          img_icon = drizzle_icon;
        } else if (response.data.weather[0].main === "Rain") {
          img_icon = rain_icon;
        } else if (response.data.weather[0].main === "Snow") {
          img_icon = snow_icon;
        } else {
          img_icon = clear_icon;
        }

        setData({
          img: img_icon,
          temp: Math.round(response.data.main.temp) + "°C",
          location: response.data.name,
          humidity: Math.round(response.data.main.humidity) + "%",
          wind: Math.round(response.data.wind.speed) + "km/h",
        });
      })
      .catch((error) => {
        setValue("");
        setErr("Invalid City Name, Please Try Agian!! ;)");
        setData({
          img: question_mark,
          temp: "xx °C",
          location: "///////",
          humidity: "xx %",
          wind: "xx km/h",
        });
      });
  };

  return (
    <div className="container">
      <div className="top-bar">
        <input
          type="text"
          className="cityInput"
          placeholder="search"
          value={value}
          onChange={onChange}
          onKeyDown={(e) => {
            if (e.key === "Enter") search();
          }}
        />

        {/* 这里如果你想要用useState来创建一个variable - value，然后用setValue来改变每次的city的value的话，如果获取这个city的value呢？

            就是说用 onChange={e => setName(e.target.value)}
        */}

        <div className="search-icon" onClick={search}>
          <img src={search_icon} alt="" className="search_icons" />
        </div>
      </div>
      <p className="error-message">{err}</p>
      <div className="weather-image">
        <img src={data.img} alt="" className="big_icons" />
      </div>
      <div className="weather-temp">{data.temp}</div>
      <div className="weather-location">{data.location}</div>
      <div className="data-container">
        <div className="element">
          <img src={humidity_icon} alt="" className="icon small_icons" />
          <div className="data">
            <div className="humidity-percent">{data.humidity}</div>
            <div className="text">Humidity</div>
          </div>
        </div>
        <div className="element">
          <img src={wind_icon} alt="" className="icon small_icons" />
          <div className="data">
            <div className="wind-rate">{data.wind}</div>
            <div className="text">Wind Speed</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Weather;
